import React from "react";


const WatchView = ({ image, sideImage }) => {
  const [isFrontView, setIsFrontView] = React.useState(true);

  const handleToggle = () => {
    setIsFrontView(!isFrontView);
  };

  return (
    <div>
      {isFrontView ? (
        <div className="front-watch">
          {image}
          <a href="#sideViewSection" onClick={handleToggle} className="toggle-link">
            Side View</a>
       </div>
           ) : (
        <div className="side-watch">
          {sideImage}
          <a href="#frontViewSection" onClick={handleToggle} className="toggle-link">
            Front View
          </a>
        </div>
       
      )}
    </div>
  );
};

export default WatchView;
